var formatNumber=function(e,t,n,r){e=parseFloat(e);if(isNaN(e))return false;t=t||2;n=n||".";r=typeof r==="undefined"?",":r;e=parseFloat(e).toFixed(t);e=e.replace(/\.|\,/g,n);var i=e.split(n);var s=i[0];var o=i[1];var u=/(\d+)(\d{3})/;while(u.test(s)){s=s.replace(u,"$1"+","+"$2")}var a=s.replace(/\,/g,r);return a+n+o}
